const request = async (url, method, body, header) => {
    try {
        if (body) {
            body = JSON.stringify(body);
            headers = 'application/json';
        }
        const response = await fetch(url, {method: method, headers: header, body: body})
        const parsedData = await  response.json();
        return parsedData;
    } catch (err) {
        console.log(err.message);
    }
}

export default request;
